import React, { FC } from "react";
import { PortalProps } from "../Portal/Portal";
export interface IBottomDrawerProps {
    /**
     * Управляет состояние компонента (открыт/закрыт)
     */
    open?: boolean;
    /**
     * обработчик закрытия компонента
     */
    onClose?: () => void;
    /**
     * Продолжительность анимации
     */
    duration?: number;
    /**
     * Скрыть скроллбар при открытом компоненте
     */
    hideScrollbars?: boolean;
    /**
     * Удалять компонент из DOM при закрытии
     */
    unmountOnExit?: boolean;
    /**
     * Добавлять компонент в DOM при открытии
     */
    mountOnEnter?: boolean;
    /**
     * Дополнительный класс для контейнера
     */
    className?: string;
    /**
     * Дополнительный класс для бэкдропа
     */
    backdropClassname?: string;
    /**
     * Дочерние элементы
     */
    children?: React.ReactNode;
    /**
     * Статическая шторка (не закрывается на свайпы и не скролиться при свайпах)
     */
    staticCurtain?: boolean;
    /**
     * Нода, компонент или функция возвращающая их
     *
     * Контейнер к которому будут добавляться порталы
     */
    container?: PortalProps["takePortalContainer"];
    /**
     * Рендерить ли в контейнер через портал.
     * @default true
     */
    usePortal?: boolean;
}
/**
 * Этот компонент создает нижнюю шторку для мобильных устройств.
 */
declare const BottomDrawer: FC<IBottomDrawerProps>;
export default BottomDrawer;
