/// <reference types="react" />
export interface IPaginationProps {
    /**
     * Режим пагинации
     */
    view: "default" | "per-page";
    /**
     * Количество страниц
     */
    count?: number;
    /**
     * Number of maximum items shown
     */
    siblingCount?: number;
    /**
     * Страница по умолчанию
     */
    defaultValue: number;
    /**
     * Обработчик изменения страницы
     */
    onChange: (page: number) => void;
    /**
     * Кнопка следующая страница (можно заменить на свою)
     */
    nextButton?: React.ReactNode;
    /**
     * Кнопка предыдущая страница (можно заменить на свою)
     */
    previousButton?: React.ReactNode;
    /**
     * Скрывает стрелки, если выбрана первая или последняя страница
     */
    hideArrows?: boolean;
}
